import styled from "styled-components";

export const Container = styled.textarea`
  height: 100px;
  padding: 8px 15px;
  border-radius: 3px;
  width: 100%;
  color: var(--secondary);
  letter-spacing: 0.1px;
  font-size: 0.938rem;
  margin-bottom: 10px;
  outline: none;
  border-color: var(--gray-light);
  border: 1px solid var(--shade);
  resize: none;
`
